import './App.css';
import React, { useCallback, useEffect } from 'react';
import Beer from './Beer';
const App = () => {
  const [beer,setBeer] = React.useState([]);
  const getBeer = async () => {
    await fetch('https://api.punkapi.com/v2/beers')
    .then(res => res.json())
    .then(
      (result) => {
        setBeer(result);
      },
      (err) => {
        throw err;
      }
    )
  }
  useEffect(() =>{
  //   GetBeer().then(function(res){
  //     setBeer(res)
  //   });
  getBeer();
  },[beer.length])

  console.log(beer)

  return (
    <div className="App">
      {beer.map( (elem,index) => {
        return(
          <Beer 
          key = {index} 
          name = {elem.name} 
          tagline = {elem.tagline} 
          first_brewed = {elem.first_brewed} 
          description = {elem.description}
          abv = {elem.abv}
          attenuation_level = {elem.attenuation_level}
          brewers_tips = {elem.brewers_tips}
          contributed_by = {elem.contributed_by}
          ebc = {elem.ebc}
          ibu = {elem.ibu}
          ph = {elem.ph}
          srm = {elem.srm}
          />
        )
      })}
    </div>
  );
}

